import MeshVisualizer from "./MeshVisualizer.js";

export default class IRenderable {
    constructor() {
        this.isIRenderable = true;
        /**
         * @type {MeshVisualizer}
         */
        this.visualizer = null;
    }
    /**
     * 
     * @param {MeshVisualizer} visualizer 
     */
    onRemoved(visualizer) { }
    /**
    * 
    * @param {MeshVisualizer} visualizer 
    */
    onAdded(visualizer) { }

    /**
     * 
     * @param {Cesium.FrameState} frameState 
     * @param {(Object3D|THREE.Object3D)[]} renderList 
     */
    render(frameState, renderList) {
        throw new Error('IRenderable.render not implemented.')
    }
}