 
import WebGLRenderItem from './WebGLRenderItem.js';
import WebGLLights from './WebGLLights.js';
import WebGLRenderer from './WebGLRenderer.js';
/**
 * 绘图命令工具，用于生成、更新、销毁Cesium.DrawCommand 
 * @param {WebGLRenderer} renderer  
 */
export default class CesiumCommands {

    constructor(renderer: WebGLRenderer)

    /**
     *  
     * @param {THREE.Mesh|THREE.Line|THREE.Points|THREE.Sprite} object 
     * @param {WebGLLights} lights
     * @param {THREE.Light[]} shadows
     * @param {number} [materialIndexOrGroupIndex] 
     * @param {ArrayBufferView} indices
     * @param {boolean} [groupOptimized]
     * @returns {WebGLRenderItem}
    */
    create(object:THREE.Mesh|THREE.Line|THREE.Points|THREE.Sprite, lights: WebGLLights, shadows: THREE.Light[], materialIndexOrGroupIndex: number, indices: ArrayBufferView, groupOptimized: boolean): WebGLRenderItem


    /**
     * 
     * @param {THREE.Mesh|THREE.Line|THREE.Points|THREE.Sprite} object 
     * @param {WebGLLights}[lights]
     * @param {THREE.Light[]}[shadows]
     * @returns {WebGLRenderItem[]}
     */
    createArray(object: THREE.Mesh | THREE.Line | THREE.Points | THREE.Sprite, lights: WebGLLights, shadows: THREE.Light[]): WebGLRenderItem[]

    /**
     * 
     * @param {THREE.Mesh|THREE.Line|THREE.Points|THREE.Sprite} object  
     * @param {THREE.BufferGeometry}geometry
     */
    update(object: THREE.Mesh | THREE.Line | THREE.Points | THREE.Sprite, geometry: THREE.BufferGeometry): void

}

