 
import WebGLRenderer from "./WebGLRenderer.js";

/**
 * 渲染状态工具，使用内置的材质或者threejs材质，生成Cesium的RenderState 
 */
export default class CesiumRenderStates {
    constructor(renderer:WebGLRenderer)
    /**
     * 
     * @param {THREE.Material} material 
     * @param {THREE.Mesh|THREE.Line|THREE.Points|THREE.Sprite} object  
     * @param {boolean} fromCache 
     * @return {Cesium.RenderState}
     */
    create(
        material: THREE.Material,
        object: THREE.Mesh | THREE.Line | THREE.Points | THREE.Sprite, 
        /**
         * @type {boolean}
         * @default false
         */
        fromCache?: boolean
    ): Cesium.RenderState

}
