 
import WebGLRenderer from './WebGLRenderer.js'; 
import WebGLLights from "./WebGLLights.js";

/**
 * 将three.js一组<几何体,材质>编译为Cesium.ShaderProgram
 */
export default class CesiumShaderPrograms {
    /**
     * 
     * @param {WebGLRenderer} renderer 
     */
    constructor(renderer: WebGLRenderer)
    /**
     * 
     * @param {THREE.Material} material 
     * @param {{[key:string]:number}} attributeLocations 
     * @param {THREE.Mesh|THREE.Line|THREE.LineLoop|THREE.LineSegments|THREE.Points|THREE.Sprite} object  
     * @returns {Cesium.ShaderProgram}
     * @private
     */
    get(material3js:THREE.Material, attributeLocations:{[key:string]:number}, object:THREE.Mesh|THREE.Line|THREE.LineLoop|THREE.LineSegments|THREE.Points|THREE.Sprite, lights:WebGLLights, shadows:THREE.Light[]): Cesium.ShaderProgram

}
