import MeshVisualizer from "../MeshVisualizer.js";
 
/**
 * 处理Threejs RenderTarget 
 */
export default class WebGLRenderTargets {

    /**
     * 处理Threejs RenderTarget 
     * @param {MeshVisualizer} visualizer 
     */
    constructor(visualizer: MeshVisualizer)

    /**
     * 
     * @param {THREE.WebGLRenderTarget|THREE.WebGLCubeRenderTarget} renderTarget
     */
    set(renderTarget: THREE.WebGLRenderTarget | THREE.WebGLCubeRenderTarget): void

    /**
     * 
     * @param {THREE.WebGLRenderTarget|THREE.WebGLCubeRenderTarget} renderTarget  
     * @returns {Cesium.Framebuffer|Cesium.Framebuffer[]}
     */
    get(renderTarget: THREE.WebGLRenderTarget | THREE.WebGLCubeRenderTarget): Cesium.Framebuffer | Cesium.Framebuffer[]

    dispose():void

}
